<?php/* Template Name: Favorites */get_header();$user_id   = get_current_user_id();$favorites = get_user_meta( $user_id, 'dw_favorites', true );$args      = [	'post_type' => 'product',	'post__in'  => $favorites];$query     = new WP_Query( $args );?>    <section class="favorites">        <div class="container">            <div class="row">				<?php if ( $query->have_posts() && $favorites ) {					$i = 1;					while ( $query->have_posts() ) {						$query->the_post();						?>                        <div class="col-xs-12 col-sm-6 col-md-3">							<?php get_template_part( 'templates/product-second' ); ?>                        </div>						<?php						if ( $i % 4 == 0 ) {							?>                            <div class="col-md-12"></div>							<?php						}						$i ++;					}				} else { ?>                    <div class="col-md-12 text-center">						<?php esc_html_e( 'No Products in favorites list', 'dw' ); ?>                    </div>				<?php }				wp_reset_postdata(); ?>            </div>        </div>    </section><?phpget_footer();